let count=0;const value=document.querySelector("#value"),buttons=document.querySelectorAll(".btn");buttons.forEach((function(t){t.addEventListener("click",(function(t){const e=t.currentTarget.classList;e.contains("decrease")?count--:e.contains("increase")?count++:count=0,value.style.color=count>0?"green":count<0?"red":"inherit",value.textContent=count}))}));